mapper.update:
		UPDATE tbl_member
		SET	user_phone = #{user_phone},
			user_gender = #{user_gender, jdbcType=VARCHAR},
			sort_local = #{sort_local, jdbcType=VARCHAR},
			sub_local = #{sub_local, jdbcType=VARCHAR}
   	   	WHERE user_id = #{user_id}

		UPDATE tbl_member
		SET	user_email = #{user_email}
   	   	WHERE user_id = #{user_id}
